def minion_game(string):

    start_time_funtion = time.time()
    
    str_len = len(string)
    print(str_len)
    vowels = set(['A', 'E', 'I', 'O', 'U'])

    s_score, k_score = 0, 0
    for i in range(str_len):
        if string[i] in vowels:
            k_score += str_len - i
        else:
            s_score += str_len - i

    if s_score == k_score:
        result = 'Draw'
    elif s_score > k_score:
        result = f'Stuart {s_score}'
    else:
        result = f'Kevin {k_score}'
    
    end_time_function = time.time()

    function_execution_time = end_time_function - start_time_funtion
    print(f"Execution time of function: {function_execution_time} seconds")

    return function_execution_time, print(result)

